import React, { Component } from "react";
import Link from "next/link";

export default class PodcastList extends Component {
  render() {
    const { audioClips } = this.props;

    return (
      <div>
        {audioClips.map(clip => (
          <Link href={`/podcast?id=${clip.id}`}>
            <a className="podcast">
              <div>{clip.title}</div>
            </a>
          </Link>
        ))}
      </div>
    );
  }
}
