import React, { Component } from "react";

export default class channel extends Component {
  static async getInitialProps({ query }) {
    let idChannel = query.id;

    // let reqChannel = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}`
    // );
    // let dataChannel = await reqChannel.json();
    // let channel = dataChannel.body.channel;

    // let reqAudios = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}/audio_clips`
    // );
    // let dataAudios = await reqAudios.json();
    // let audioClips = dataAudios.body.audio_clips;

    // let reqSeries = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}/child_channels`
    // );
    // let dataSeries = await reqSeries.json();
    // let series = dataSeries.body.channels;

    let [reqChannel, reqSeries, reqAudios] = await Promise.all([
      fetch(`https://api.audioboom.com/channels/${idChannel}`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
    ]);

    let dataChannel = await reqChannel.json();
    let channel = dataChannel.body.channel;

    let dataAudios = await reqAudios.json();
    let audioClips = dataAudios.body.audio_clips;

    let dataSeries = await reqSeries.json();
    let series = dataSeries.body.channels;

    return { channel, audioClips, series };
  }
  render() {
    const { channel, audioClips, series } = this.props;
    return (
      <div>
        <header>Podcasts</header>
        <h1>{channel.title}</h1>

        <h2>Series</h2>
        {series.map(serie => (
          <div>{serie.title}</div>
        ))}

        <h2>Ultimos Podcasts</h2>
        {audioClips.map(clip => (
          <div>{clip.title}</div>
        ))}

        <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }
          h1 {
            font-weight: 600;
            padding: 15px;
          }
        `}</style>
        <style jsx global>{`
          body {
            margin: 0;
            font-family: system-ui;
            background: white;
          }
        `}</style>
      </div>
    );
  }
}
