import React, { Component } from "react";
import Layout from "../components/Layout";
import ChannelGrid from "../components/ChannelGrid";
import PodcastList from "../components/PodcastList";
import PodcastListWithClick from "../components/PodcastListWithClick";
import PodcastPlayer from "../components/PodcastPlayer";
import Player from "../components/Player";
import Error from "./_error";

export default class channel extends Component {
  constructor(props) {
    super(props);
    this.state = { openPodcast: null };
  }

  static async getInitialProps({ query, res }) {
    let idChannel = query.id;
    try {
      let [reqChannel, reqSeries, reqAudios] = await Promise.all([
        fetch(`https://api.audioboom.com/channels/${idChannel}`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
      ]);

      if (reqChannel.status >= 400) {
        res.statusCode = reqChannel.status;
        return {
          channel: null,
          audioClips: null,
          series: null,
          statusCode: reqChannel.status
        };
      }

      let dataChannel = await reqChannel.json();
      let channel = dataChannel.body.channel;

      let dataAudios = await reqAudios.json();
      let audioClips = dataAudios.body.audio_clips;

      let dataSeries = await reqSeries.json();
      let series = dataSeries.body.channels;

      return { channel, audioClips, series, statusCode: 200 };
    } catch (e) {
      return {
        channel: null,
        audioClips: null,
        series: null,
        statusCode: 503
      };
    }
    // let reqChannel = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}`
    // );
    // let dataChannel = await reqChannel.json();
    // let channel = dataChannel.body.channel;
    // let reqAudios = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}/audio_clips`
    // );
    // let dataAudios = await reqAudios.json();
    // let audioClips = dataAudios.body.audio_clips;
    // let reqSeries = await fetch(
    //   `https://api.audioboom.com/channels/${idChannel}/child_channels`
    // );
    // let dataSeries = await reqSeries.json();
    // let series = dataSeries.body.channels;
  }

  openPodcast = (event, podcast) => {
    event.preventDefault();
    this.setState({ openPodcast: podcast });
  };

  closePodcast = event => {
    event.preventDefault();
    this.setState({ openPodcast: null });
  };

  render() {
    const { channel, audioClips, series, statusCode } = this.props;
    const { openPodcast } = this.state;

    if (statusCode !== 200) {
      return <Error statusCode={statusCode} />;
    }

    return (
      <Layout title={channel.title}>
        {openPodcast && (
          <div className="modal">
            <PodcastPlayer podcast={openPodcast} onClose={this.closePodcast}>
              <Player podcast={podcast} />
            </PodcastPlayer>
          </div>
        )}
        <h1>{channel.title}</h1>

        {series.length > 0 && (
          <div>
            <h2>Series</h2>
            <ChannelGrid channels={series} />
          </div>
        )}

        <h2>Ultimos Podcasts</h2>
        {/* <PodcastList audioClips={audioClips} /> */}
        <PodcastListWithClick
          podcasts={audioClips}
          onClickPodcast={this.openPodcast}
        />

        <style jsx>{`
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
          .modal {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            z-index: 99999;
            background: black;
          }
        `}</style>
      </Layout>
    );
  }
}
