import React, { Component } from "react";
import Link from "next/link";
import Player from "../components/Player";
import Layout from "../components/Layout";

export default class podcast extends Component {
  static async getInitialProps({ query }) {
    let idPodcast = query.id;

    let reqPodcast = await fetch(
      `https://api.audioboom.com/audio_clips/${idPodcast}.mp3`
    );
    let dataPodcast = await reqPodcast.json();
    let podcast = dataPodcast.body.audio_clip;

    return { podcast };
  }
  render() {
    const { podcast } = this.props;

    return (
      <Layout title={podcast.title}>
        <Modal podcast={podcast}></Modal>

        <div className="modal">
          <div className="clip">
            <nav>
              <Link href={`/channel?id=${podcast.channel.id}`}>
                <a className="close">&lt; Volver</a>
              </Link>
            </nav>

            <picture>
              <div
                style={{
                  backgroundImage: `url(${podcast.urls.image ||
                    podcast.channel.urls.logo_image.original})`
                }}
              />
            </picture>

            <Player podcast={podcast} />
          </div>
        </div>

        <style jsx>{`
          nav {
            background: none;
          }
          nav a {
            display: inline-block;
            padding: 15px;
            color: white;
            cursor: pointer;
            text-decoration: none;
          }
          .clip {
            display: flex;
            height: 100%;
            flex-direction: column;
            background: #8756ca;
            color: white;
          }
          picture {
            display: flex;
            align-items: center;
            justify-content: center;
            flex: 1 1;
            flex-direction: column;
            width: auto;
            padding: 10%;
          }
          picture div {
            width: 100%;
            height: 100%;
            background-position: 50% 50%;
            background-size: contain;
            background-repeat: no-repeat;
          }
          .player {
            padding: 30px;
            background: rgba(0, 0, 0, 0.3);
            text-align: center;
          }
          h3 {
            margin: 0;
          }
          h6 {
            margin: 0;
            margin-top: 1em;
          }
          audio {
            margin-top: 2em;
            width: 100%;
          }

          .modal {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            z-index: 99999;
          }
        `}</style>
      </Layout>
    );
  }
}
